  const pageTitle = pathToTitleMap[location.pathname] || '客户成功系统';
  console.log('CustomerSuccess pageTitle:', pageTitle);

  // 如果是工作台页面，显示新的工作台界面
  if (location.pathname === '/dashboard/work') {
    console.log('Rendering WorkbenchDashboard');
    return (
      <div style={{ padding: '24px', background: '#fff' }}>
        <h1>工作台页面加载成功</h1>
        <p>当前路径: {location.pathname}</p>
        <p>页面标题: {pageTitle}</p>
        <WorkbenchDashboard />
      </div>
    );
  }
  // 客户分层矩阵
  if (location.pathname === '/dashboard/layers') {
    return <TieringMatrix />;
  }

  // 根据页面标题生成内容
  const content = generateTabContent(pageTitle);

  return (
    <div style={{ 
