  marginBottom: '16px',
};

// 概览与监控页签组件
const OverviewTab: React.FC = () => {
  const navigate = useNavigate();
  const [customerFilter, setCustomerFilter] = useState<CustomerFilter>({});
  const [currentPage, setCurrentPage] = useState(1);
  const pageSize = 10;

  // 过滤客户数据
  const filteredCustomers = useMemo(() => {
    return mockCustomers.filter(customer => {
      if (customerFilter.name && !customer.name.toLowerCase().includes(customerFilter.name.toLowerCase())) {
        return false;
      }
      if (customerFilter.healthLevel && customer.healthLevel !== customerFilter.healthLevel) {
        return false;
      }
      if (customerFilter.lifecycleStage && customer.lifecycleStage !== customerFilter.lifecycleStage) {
        return false;
      }
      if (customerFilter.renewalRisk !== undefined && customer.isRenewalRisk !== customerFilter.renewalRisk) {
        return false;
      }
      return true;
    });
  }, [customerFilter]);

  // 健康度分布数据
  const healthDistributionData = [
    { name: '健康', value: mockServiceOverview.healthDistribution.healthy, color: healthColors['健康'] },
    { name: '一般', value: mockServiceOverview.healthDistribution.normal, color: healthColors['一般'] },
    { name: '风险', value: mockServiceOverview.healthDistribution.risky, color: healthColors['风险'] }
  ];

  // 异动情况与重点关注客户的示例数据
  const movementEvents = [
    { id: 'm1', title: '管理员离职', detail: '客户A 主要管理员离职', date: '2025-01-05', level: 'high' },
    { id: 'm2', title: 'CSM变更', detail: '客户B CSM 负责人调整', date: '2025-01-08', level: 'medium' },
    { id: 'm3', title: '权限收缩', detail: '客户C 减少管理员数量', date: '2025-01-12', level: 'low' },
  ];

  const focusCustomers = [
    { id: 'f1', name: '华夏教育集团', tags: ['培训部>3人', '教育行业', '成交额>¥50万'] },
    { id: 'f2', name: '星云制造', tags: ['制造业', '增购潜力高'] },
    { id: 'f3', name: '新锐互联网', tags: ['互联网科技', '培训部>3人'] },
  ];

  // 可配置查询条件（本地状态）
  const [movementLevel, setMovementLevel] = useState<'all' | 'high' | 'medium' | 'low'>('all');
  const [movementKeyword, setMovementKeyword] = useState('');

  const [focusMinAmount, setFocusMinAmount] = useState<number>(50);
  const [focusIndustries, setFocusIndustries] = useState<string[]>([]);
  const [focusMinTrainingSize, setFocusMinTrainingSize] = useState<number>(3);

  const filteredMovements = useMemo(() => {
    return movementEvents.filter(e => {
      const levelOk = movementLevel === 'all' || e.level === movementLevel;
      const kw = movementKeyword.trim();
      const kwOk = kw === '' || e.title.includes(kw) || e.detail.includes(kw);
      return levelOk && kwOk;
    });
  }, [movementEvents, movementLevel, movementKeyword]);

  // 将示例 tags 转换为结构化信息进行过滤
  const normalizedFocus = useMemo(() => {
    return focusCustomers.map(c => {
      const industry = c.tags.find(t => t.includes('行业')) || c.tags.find(t => ['制造业','互联网科技','教育行业'].some(k=>t.includes(k))) || '其他';
      const amountTag = c.tags.find(t => t.startsWith('成交额>¥'));
      const amount = amountTag ? parseInt(amountTag.replace(/[^\d]/g, ''), 10) / 10000 : 30; // 万元
      const trainingTag = c.tags.find(t => t.includes('培训部'));
      const sizeMatch = trainingTag ? parseInt(trainingTag.replace(/[^\d]/g, ''), 10) : 0;
      return { ...c, industry, amountWan: amount, trainingSize: sizeMatch };
    });
  }, [focusCustomers]);

  const filteredFocus = useMemo(() => {
    return normalizedFocus.filter(c => {
      const amountOk = c.amountWan >= focusMinAmount;
      const industryOk = focusIndustries.length === 0 || focusIndustries.some(i => c.industry.includes(i));
      const trainingOk = c.trainingSize >= focusMinTrainingSize;
      return amountOk && industryOk && trainingOk;
    });
  }, [normalizedFocus, focusMinAmount, focusIndustries, focusMinTrainingSize]);

  // 表格列定义
  const columns = [
    {
      title: '客户名称',
      dataIndex: 'name',
      key: 'name',
      width: 200,
      render: (text: string) => (
        <div style={{ display: 'flex', alignItems: 'center' }}>
          <Avatar size={32} icon={<UserOutlined />} style={{ marginRight: 8 }} />
          <span style={{ fontWeight: 500 }}>{text}</span>
        </div>
      )
    },
    {
      title: 'CSM',
      dataIndex: 'csm',
      key: 'csm',
      width: 100
    },
    {
      title: '健康分',
      dataIndex: 'healthScore',
      key: 'healthScore',
      width: 120,
      render: (score: number, record: Customer) => (
        <div style={{ display: 'flex', alignItems: 'center' }}>
          <Progress 
            percent={score} 
            size="small" 
            strokeColor={healthColors[record.healthLevel]}
            showInfo={false}
            style={{ width: 60, marginRight: 8 }}
          />
          <span style={{ color: healthColors[record.healthLevel], fontWeight: 500 }}>{score}</span>
        </div>
      )
    },
    {
      title: '健康等级',
      dataIndex: 'healthLevel',
      key: 'healthLevel',
      width: 100,
      render: (level: HealthLevel) => (
        <Tag color={healthColors[level]}>{level}</Tag>
      )
    },
    {
      title: '生命周期',
      dataIndex: 'lifecycleStage',
      key: 'lifecycleStage',
      width: 100,
      render: (stage: LifecycleStage) => (
        <Tag color={lifecycleColors[stage]}>{stage}</Tag>
      )
    },
    {
      title: '合同金额',
      dataIndex: 'contractAmount',
      key: 'contractAmount',
      width: 120,
      render: (amount: number) => `¥${amount.toLocaleString()}`
    },
    {
      title: '续费风险',
      dataIndex: 'isRenewalRisk',
      key: 'isRenewalRisk',
      width: 100,
      render: (isRisk: boolean) => (
        <Badge 
          status={isRisk ? 'error' : 'success'} 
          text={isRisk ? '是' : '否'} 
        />
      )
    },
    {
      title: '最后接触',
      dataIndex: 'lastContactDate',
      key: 'lastContactDate',
      width: 120,
      render: (date: string) => new Date(date).toLocaleDateString()
    },
    {
      title: '操作',
      key: 'action',
      width: 120,
      render: (_: any, record: Customer) => (
        <Button 
          type="link" 
          size="small"
          onClick={() => navigate(`/profiles/service/${record.id}`)}
        >
          查看详情
        </Button>
      )
    }
  ];

  return (
    <div style={{ padding: '24px' }}>
      {/* 核心指标卡片 - 采用工作看板的现代化风格 */}
      <Row gutter={[16, 16]} style={{ marginBottom: 24 }}>
        <Col xs={24} sm={8}>
          <Card style={cardStyle}>
            <div style={{ textAlign: 'center' }}>
              <div style={{ fontSize: '32px', fontWeight: '700', color: '#1890ff', marginBottom: '8px' }}>
                {mockServiceOverview.totalCustomers}
              </div>
              <Text type="secondary" style={{ fontSize: '14px' }}>总客户数</Text>
              <div style={{ display: 'flex', alignItems: 'center', justifyContent: 'center', marginTop: '8px' }}>
                <ArrowUpOutlined style={{ color: '#52c41a', fontSize: '12px', marginRight: '4px' }} />
                <Text style={{ color: '#52c41a', fontSize: '12px' }}>+12</Text>
              </div>
            </div>
          </Card>
        </Col>
        <Col xs={24} sm={8}>
          <Card style={cardStyle}>
            <div style={{ textAlign: 'center' }}>
              <div style={{ fontSize: '32px', fontWeight: '700', color: '#52c41a', marginBottom: '8px' }}>
                {mockServiceOverview.avgHealthScore}
              </div>
              <Text type="secondary" style={{ fontSize: '14px' }}>平均健康分</Text>
              <div style={{ display: 'flex', alignItems: 'center', justifyContent: 'center', marginTop: '8px' }}>
                <ArrowUpOutlined style={{ color: '#52c41a', fontSize: '12px', marginRight: '4px' }} />
                <Text style={{ color: '#52c41a', fontSize: '12px' }}>+3.2</Text>
              </div>
            </div>
          </Card>
        </Col>
        <Col xs={24} sm={8}>
          <Card style={cardStyle}>
            <div style={{ textAlign: 'center' }}>
              <div style={{ fontSize: '32px', fontWeight: '700', color: '#f5222d', marginBottom: '8px' }}>
                {mockServiceOverview.riskCustomers}
              </div>
              <Text type="secondary" style={{ fontSize: '14px' }}>风险客户数</Text>
              <div style={{ display: 'flex', alignItems: 'center', justifyContent: 'center', marginTop: '8px' }}>
                <ArrowDownOutlined style={{ color: '#ff4d4f', fontSize: '12px', marginRight: '4px' }} />
                <Text style={{ color: '#ff4d4f', fontSize: '12px' }}>-2</Text>
              </div>
            </div>
          </Card>
        </Col>
        {/* 健康度分布（饼图） */}
        <Col xs={24} sm={8}>
          <Card style={cardStyle}>
            <div style={{ display: 'flex', alignItems: 'center', marginBottom: 8 }}>
              <HeartOutlined style={{ color: '#f5222d', marginRight: 8 }} />
              <span style={{ fontSize: 14, fontWeight: 600 }}>健康度分布</span>
            </div>
            <div style={{ display: 'flex', alignItems: 'center', gap: 16 }}>
              <div style={{ position: 'relative', width: 88, height: 88, borderRadius: '50%', background: `conic-gradient(${healthDistributionData.map((d, idx, arr) => {
                const total = arr.reduce((s, i) => s + i.value, 0);
                const start = arr.slice(0, idx).reduce((s, i) => s + i.value, 0) / total * 360;
                const end = (start + d.value / total * 360);
                return `${d.color} ${start}deg ${end}deg`;
              }).join(', ')})` }} />
              <div style={{ display: 'grid', gap: 8 }}>
                {healthDistributionData.map((d) => (
                  <div key={d.name} style={{ display: 'flex', alignItems: 'center', gap: 8 }}>
                    <div style={{ width: 10, height: 10, borderRadius: 2, background: d.color }} />
                    <Text>{d.name}：{d.value}</Text>
                  </div>
                ))}
              </div>
            </div>
          </Card>
        </Col>
      </Row>

      {/* 数据分布卡片 - 同一行展示三个卡片：健康度分布、异动情况、重点关注客户 */}
      <Row gutter={[16, 16]} style={{ marginBottom: 16 }}>
        {/* 健康度分布 */}
        <Col xs={24} lg={8}>
          <Card 
            style={{
              ...cardStyle,
              marginBottom: 0,
              padding: '16px'
            }}
            bodyStyle={{ padding: '12px 0' }}
            title={
              <div style={{ display: 'flex', alignItems: 'center' }}>
                <HeartOutlined style={{ color: '#f5222d', marginRight: '8px' }} />
                <span style={{ fontSize: '16px', fontWeight: '600' }}>客户健康度分布</span>
              </div>
            }
          >
            <div style={{ padding: '12px 0' }}>
              {healthDistributionData.map(item => (
                <div key={item.name} style={{ marginBottom: 12 }}>
                  <div style={{ display: 'flex', justifyContent: 'space-between', marginBottom: 6 }}>
                    <span style={{ display: 'flex', alignItems: 'center', gap: 8 }}>
                      <div style={{ width: 12, height: 12, backgroundColor: item.color, borderRadius: 2 }} />
                      {item.name}
                    </span>
                    <span style={{ fontWeight: 500 }}>{item.value}个</span>
                  </div>
                  <Progress 
                    percent={(item.value / mockServiceOverview.totalCustomers) * 100} 
                    strokeColor={item.color}
                    showInfo={false}
                    size="small"
                  />
                </div>
              ))}
            </div>
          </Card>
        </Col>

        {/* 异动情况 */}
        <Col xs={24} lg={8}>
          <Card 
            style={{
              ...cardStyle,
              marginBottom: 0,
              padding: '16px'
            }}
            bodyStyle={{ padding: '12px 0' }}
            title={
              <div style={{ display: 'flex', alignItems: 'center', justifyContent: 'space-between' }}>
                <div style={{ display: 'flex', alignItems: 'center' }}>
                  <AlertOutlined style={{ color: '#fa8c16', marginRight: '8px' }} />
                  <span style={{ fontSize: '16px', fontWeight: '600' }}>异动情况</span>
                </div>
                <Space size={8}>
                  <Select size="small" value={movementLevel} onChange={setMovementLevel} style={{ width: 96 }}>
                    <Option value="all">全部</Option>
                    <Option value="high">高</Option>
                    <Option value="medium">中</Option>
                    <Option value="low">低</Option>
                  </Select>
                  <Input size="small" placeholder="关键词" value={movementKeyword} onChange={(e)=>setMovementKeyword(e.target.value)} style={{ width: 120 }} />
                </Space>
              </div>
            }
          >
            <div style={{ display: 'grid', gap: 12 }}>
              {filteredMovements.map(e => (
                <Card key={e.id} size="small" style={{ borderRadius: 8 }}>
                  <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
                    <div>
                      <Text strong style={{ marginRight: 8 }}>{e.title}</Text>
                      <Text type="secondary" style={{ fontSize: 12 }}>{e.detail}</Text>
                    </div>
                    <div style={{ display: 'flex', alignItems: 'center', gap: 8 }}>
                      <Tag color={e.level === 'high' ? 'red' : e.level === 'medium' ? 'orange' : 'default'}>
                        {e.level === 'high' ? '高' : e.level === 'medium' ? '中' : '低'}
                      </Tag>
                      <Text type="secondary" style={{ fontSize: 12 }}>{e.date}</Text>
                    </div>
                  </div>
                </Card>
              ))}
            </div>
          </Card>
        </Col>

        {/* 重点关注客户 */}
        <Col xs={24} lg={8}>
          <Card 
            style={{
              ...cardStyle,
              marginBottom: 0,
              padding: '16px'
            }}
            bodyStyle={{ padding: '12px 0' }}
            title={
              <div style={{ display: 'flex', alignItems: 'center', justifyContent: 'space-between' }}>
                <div style={{ display: 'flex', alignItems: 'center' }}>
                  <StarOutlined style={{ color: '#722ed1', marginRight: '8px' }} />
                  <span style={{ fontSize: '16px', fontWeight: '600' }}>重点关注客户</span>
                </div>
                <Space size={8} wrap>
                  <span style={{ fontSize: 12, color: '#666' }}>成交额(万元)≥</span>
                  <InputNumber size="small" min={0} value={focusMinAmount} onChange={(v)=>setFocusMinAmount(Number(v))} style={{ width: 80 }} />
                  <Select
                    size="small"
                    mode="multiple"
                    allowClear
                    placeholder="行业"
                    value={focusIndustries}
                    onChange={setFocusIndustries}
                    style={{ minWidth: 120 }}
                  >
                    <Option value="教育">教育</Option>
                    <Option value="制造">制造</Option>
                    <Option value="互联网">互联网</Option>
                  </Select>
                  <span style={{ fontSize: 12, color: '#666' }}>培训部≥</span>
                  <InputNumber size="small" min={0} value={focusMinTrainingSize} onChange={(v)=>setFocusMinTrainingSize(Number(v))} style={{ width: 60 }} />
                </Space>
              </div>
            }
          >
            <div style={{ display: 'grid', gap: 12 }}>
              {filteredFocus.map(c => (
                <Card key={c.id} size="small" style={{ borderRadius: 8 }}>
                  <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
                    <div style={{ display: 'flex', alignItems: 'center', gap: 8 }}>
                      <Avatar size={24} icon={<UserOutlined />} />
                      <Text strong>{c.name}</Text>
                    </div>
                    <div style={{ display: 'flex', gap: 6, flexWrap: 'wrap', justifyContent: 'flex-end' }}>
                      {c.tags.map((t, i) => (
                        <Tag key={i} color={i % 3 === 0 ? 'blue' : i % 3 === 1 ? 'purple' : 'green'} style={{ borderRadius: 6 }}>
                          {t}
                        </Tag>
                      ))}
                    </div>
                  </div>
                </Card>
              ))}
            </div>
          </Card>
        </Col>
      </Row>

      {/* 客户列表 - 采用交接实施页面的现代化风格 */}
      <div style={{ 
        background: '#fff',
        borderRadius: '8px',
        padding: '24px',
        boxShadow: '0 1px 2px rgba(0, 0, 0, 0.03)'
      }}>
        {/* 筛选器 - 采用交接实施页面的筛选风格 */}
        <div style={{ 
          marginBottom: 16, 
          padding: 16, 
          background: '#fafafa', 
          borderRadius: '8px',
          border: '1px solid #f0f0f0'
        }}>
          <Row gutter={[16, 16]} align="middle">
            <Col xs={24} sm={6}>
              <Input
                placeholder="搜索客户名称"
                prefix={<SearchOutlined />}
                value={customerFilter.name}
                onChange={e => setCustomerFilter(prev => ({ ...prev, name: e.target.value }))}
                style={{ borderRadius: '6px' }}
              />
            </Col>
            <Col xs={24} sm={5}>
              <Select
                placeholder="健康等级"
                style={{ width: '100%', borderRadius: '6px' }}
                value={customerFilter.healthLevel}
                onChange={value => setCustomerFilter(prev => ({ ...prev, healthLevel: value }))}
                allowClear
              >
                <Option value="健康">健康</Option>
                <Option value="一般">一般</Option>
                <Option value="风险">风险</Option>
              </Select>
            </Col>
            <Col xs={24} sm={5}>
              <Select
                placeholder="生命周期"
                style={{ width: '100%', borderRadius: '6px' }}
                value={customerFilter.lifecycleStage}
                onChange={value => setCustomerFilter(prev => ({ ...prev, lifecycleStage: value }))}
                allowClear
              >
                <Option value="成长期">成长期</Option>
                <Option value="成熟期">成熟期</Option>
                <Option value="衰退期">衰退期</Option>
              </Select>
            </Col>
            <Col xs={24} sm={5}>
              <Select
                placeholder="续费风险"
                style={{ width: '100%', borderRadius: '6px' }}
                value={customerFilter.renewalRisk}
                onChange={value => setCustomerFilter(prev => ({ ...prev, renewalRisk: value }))}
                allowClear
              >
                <Option value={true}>是</Option>
                <Option value={false}>否</Option>
              </Select>
            </Col>
            {/* 操作组：搜索（最左，蓝色图标按钮）、导出、刷新 */}
            <Col xs={24} sm={3} style={{ display: 'flex', justifyContent: 'flex-end', gap: 8 }}>
              <Button type="primary" shape="circle" icon={<SearchOutlined />} onClick={() => message.success('搜索完成')} />
              <Button type="text" icon={<ExportOutlined />} onClick={() => message.success('导出功能开发中')} />
              <Button type="text" icon={<ReloadOutlined />} onClick={() => message.success('数据已刷新')} />
            </Col>
          </Row>
        </div>

        <Table
          columns={columns}
          dataSource={filteredCustomers}
          rowKey="id"
          pagination={{
            current: currentPage,
            pageSize: pageSize,
            total: filteredCustomers.length,
            onChange: setCurrentPage,
            showSizeChanger: false,
            showQuickJumper: true,
            showTotal: (total, range) => `第 ${range[0]}-${range[1]} 条，共 ${total} 条`
          }}
          size="middle"
          style={{ background: '#fff' }}
        />
      </div>
    </div>
  );
};

