  const [newRecordModalVisible, setNewRecordModalVisible] = useState(false);
  const [form] = Form.useForm();

  // 模拟客户数据
  useEffect(() => {
    const mockCustomer = {
      id: '1',
      name: '北京科技有限公司',
      healthLevel: '健康',
      healthScore: 85,
      lifecycle: '成长期',
      renewalRisk: '低',
      industry: '互联网科技',
      companySize: '500-1000人',
      address: '北京市朝阳区建国门外大街1号',
      contractAmount: 500000,
      contractEndDate: '2026-08-31',
      purchasedProducts: ['产品A', '插件B', '高级服务'],
      keyContacts: [
        {
          id: '1',
          name: '张总',
          title: '技术总监',
          phone: '138-0013-8000',
          email: 'zhang@company.com',
          isPrimary: true
        }
      ],
      serviceRecords: [
        {
          id: '1',
          type: 'phone',
          title: '客户回访电话',
          content: '与张总进行了30分钟的产品使用情况回访',
          operator: 'CSM-王五',
          timestamp: '2024-01-15 14:30'
        }
      ]
    };

    setCustomer(mockCustomer);
  }, [id]);

  // 获取健康度颜色
  const getHealthColor = (level: string) => {
    switch (level) {
      case '健康': return 'success';
      case '一般': return 'warning';
      case '风险': return 'error';
      default: return 'default';
    }
  };

  if (!customer) {
    return <div style={{ padding: '24px', textAlign: 'center' }}>加载中...</div>;
  }

  return (
    <div style={{
      padding: '24px',
      background: '#f5f5f5',
      minHeight: 'calc(100vh - 120px)'
    }}>
