// 续约客户数据接口
interface RenewalCustomer {
  key: string;
  customerName: string;
  healthScore: number;
  healthLevel: CustomerHealth;
  contractExpiryDate: string;
  renewalAmount: number;
  daysToExpiry: number;
  status: RenewalStatus;
  riskReason?: string;
  opportunityPoint?: string;
  owner: string;
  lastInteraction: string;
  nextAction: string;
}

// 续约动态接口
interface RenewalActivity {
  id: string;
  customerName: string;
  activity: string;
  timestamp: string;
  type: 'status_update' | 'health_change' | 'interaction' | 'quote_sent';
  icon: React.ReactNode;
  color: string;
}

// 续约剧本接口
interface RenewalPlaybook {
  id: string;
  title: string;
  description: string;
  steps: string[];
  applicableFor: RenewalStatus[];
}

// 模拟数据
const mockRenewalCustomers: RenewalCustomer[] = [
