  },
  {
    key: '4',
    customerName: '美团点评',
    healthScore: 88,
    healthLevel: '健康',
    contractExpiryDate: '2024-02-28',
    renewalAmount: 600000,
    daysToExpiry: 8,
    status: '已续约',
    owner: '赵敏',
    lastInteraction: '2024-02-26',
    nextAction: '续约后服务'
  },
  {
    key: '5',
    customerName: '滴滴出行',
    healthScore: 35,
    healthLevel: '风险',
    contractExpiryDate: '2024-03-10',
