  }
];

// 续约动态数据
const mockRenewalActivities: RenewalActivity[] = [
  {
    id: '1',
    customerName: '腾讯科技',
    activity: '续约状态更新为"流失风险"',
    timestamp: '2024-02-28 14:30',
    type: 'status_update',
    icon: <ExclamationCircleOutlined />,
    color: '#fa541c'
  },
  {
    id: '2',
    customerName: '阿里巴巴',
    activity: '健康分从85提升到92',
    timestamp: '2024-02-27 16:15',
    type: 'health_change',
    icon: <RiseOutlined />,
    color: '#52c41a'
  },
  {
    id: '3',
    customerName: '字节跳动',
    activity: '发送续约报价单',
