  // 如果是配置或分析页面
  const editMatch = pathname.match(/\/ai-tools\/consultant\/([^\/]+)\/edit/);
  const analyticsMatch = pathname.match(/\/ai-tools\/consultant\/([^\/]+)\/analytics/);

  if (editMatch || analyticsMatch) {
    const agentId = editMatch ? editMatch[1] : analyticsMatch![1];
