        <WorkbenchDashboard />
      </div>
    );
  }
  // 客户分层矩阵
  if (location.pathname === '/dashboard/layers') {
    return <TieringMatrix />;
  }

  // 根据页面标题生成内容
  const content = generateTabContent(pageTitle);

  return (
    <div style={{ 
      padding: '24px',
      background: '#fafafa',
      minHeight: 'calc(100vh - 120px)', // 调整高度，为tab栏和footer留出空间
      paddingBottom: '60px' // 为footer留出底部间距
    }}>
      {content}
    </div>
  );
};

export default CustomerSuccess;

